document.addEventListener("DOMContentLoaded",function(){let c=document.querySelectorAll(".commitment-item"),a=document.querySelectorAll(".commitment-detail");c.forEach((e,t)=>{e.addEventListener("click",()=>{c.forEach(e=>e.classList.remove("active")),a.forEach(e=>e.classList.remove("active")),e.classList.add("active"),a[t].classList.add("active")})})});