document.addEventListener("DOMContentLoaded",function(){let t=document.querySelectorAll(".service-item"),c=document.querySelectorAll(".service-detail");t.forEach(e=>{e.addEventListener("click",function(){t.forEach(e=>e.classList.remove("active")),c.forEach(e=>e.classList.remove("active")),this.classList.add("active");var e=this.getAttribute("data-service");document.getElementById(e+"-content").classList.add("active")})})});